import React, { useState } from "react";
import { Link } from "react-router-dom";
import './Home.css';

const Home = () => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");

  var get_name = window.location.href.match(/([^\/]*)\/*$/)[1];
  if(get_name === "localhost:3000")
    {
    get_name = "";
    }

  function setStates(p_room)
  {
    setRoom(p_room);

    if(name == ""){
      setName(get_name);
    }
  }

  return (
    <div className="wrapper fadeIn Down">
      <Link to="/welcome">
        <button className="button-back">
          <img src={process.env.PUBLIC_URL + "/icon-info.svg"} alt="Welcome" title="Welcome"/>
        </button>
      </Link>
      <Link to="/dashboard">
        <button style={{width:"70px"}} className="btn btn-light mb-3">
          <img src={process.env.PUBLIC_URL + "/icon-dashboard.svg"} alt="Home" title="Dashboard"/>
        </button>
      </Link>
      <div className="form-login">
        <div className="display-3">Welcome to Hi5</div>
      <div className="row">
        <div className="col border-right border-dark">
          <form className="was-validated">
            <div>
              <input
                defaultValue={get_name}
                id="name"
                className="form-control mt-3"
                placeholder="Name"
                type="text"
                onChange={(event) => setName(event.target.value)}
                required
              />
              <div className="invalid-feedback">Bitte Nutzernamen eingeben!</div>
            </div>
            <div>
              <input
                className="form-control mt-3"
                placeholder="Room"
                type="text"
                onChange={(event) => setStates(event.target.value)}
              />
            </div>
            <Link
              onClick={(e) => (!name || !room ? e.preventDefault() : null)}
              to={`/chat?name=${name}&room=${room}`}
            >
              <button className="btn btn-primary mt-3" type="submit">Sign In</button>
            </Link>
          </form>
      </div>
      <div className="col">
        <Link
            onClick={(e) => (!name ? e.preventDefault() : null)}
            to={`/chat?name=${name}&room=room-${Math.floor(Math.random()* (999999999 - 100000000) + 100000000)}`}
        >
          <button
            className="btn btn-dark mt-3"
            onMouseEnter={() =>setStates(room)}>Create Room</button>
        </Link>
      </div>
     </div>
    </div>
    </div>
  );
};

export default Home;
