import React, { useState, useEffect, useRef } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import './Chat.css'
let socket;
var master = false;

const Chat = ({ location }) => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [pokerState, setPokerState] = useState(false);
  //const [pokerMaster, setPokerMaster] = useState(false);
  const [voted, setVote] = useState(false);

  const divRef = useRef(null);

  const ENDPOINT = "http://localhost:5000";

  useEffect(() => {
    const { name, room } = queryString.parse(location.search);
    socket = io(ENDPOINT);
    setRoom(room);
    setName(name);

    socket.emit("join", { name, room }, (error) => {
      if (error) {
        alert(error);
      }
    });
  }, [location.search]);

  /*useEffect(() => {
    divRef.current.scrollIntoView({ behavior: 'smooth' });
  }, []);*/


  useEffect(() => {
    socket.on("message", (message) => {
      setMessages((messages) => [...messages, message]);
    });
    // socket.on("roomData", ({ users }) => {
    //   console.log(users);
    //   setUsers(users);
    // });
  }, []); 

  useEffect(() => {
    socket.on("planningPoker", () => {
      setPokerState(true);
      console.log("State of PlanningPoker: " + pokerState);
    });
  }, []);

  /*useEffect(() => {
    socket.on("pokerMaster", () => {
      setPokerState([true, true]);
    });
  });*/

  useEffect(() => {
    socket.on("endPoker", () => {
      setPokerState(false);
      setVote(false);
    });
  }); 

  const handleSubmit = (e) => { 
    e.preventDefault();
    if (message) {
      if(message[0] == '#') {
        console.log("# erkannt Client");
        if(!pokerState)
          master = true;
      }
      console.log(master);
      socket.emit("sendMessage", { message });
      setMessage("");
    } else {
      console.log("empty chat input");
    }
  }; 

  function printPokerValue(wert) {
    //e.preventDefault();
    console.log("funktion ausgef체hrt");
    //setMessage("Gew채hlter Wert: " + value);
    socket.emit("setPokerValue", wert);
    setVote(true);
  }

  function endPoker() {  
    master = false;
    setPokerState(false);    
    socket.emit("endPoker");
  }
  
  return (
    <div className="container">
      <div className="personal_info">
        <h1>Hi5 {pokerState}</h1>
      </div>
      <div className="contact_info">
        <h1>{name}</h1> 
      </div>
      <div ref={divRef} className="chat">
      {messages.map((val, i) => {
        return (
          <div key={i} className="chatMessage">
            <b>{val.user}</b>
             <br />
            <div>
              {val.text.split("\n").map((val2, j) => {
                return (
                  <div key={i+"m"+j}>
                    {val2}
                    <br />
                  </div>
                );
              })}
            </div>
          </div> 
        );
      })} 
      <div align="center">
        {pokerState && !voted && (
          
          <div>
            <div className="label-poker-heading">Bitte geben Sie Ihre Wertung ab:</div>
            <div className="pokerLabel"> 
              <button className="button-planning-poker" onClick={() => printPokerValue(0)}>0</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(0.5)}>{"\u00BD"}</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(1)}>1</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(2)}>2</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(3)}>3</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(5)}>5</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(8)}>8</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(10)}>10</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(13)}>13</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(20)}>20</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(40)}>40</button>
              <button className="button-planning-poker" onClick={() => printPokerValue(100)}>100</button>
            </div> 
            
          </div> 
        )}
        {master && pokerState &&(
              <div>
                <button className="button-end-planning-poker" onClick={() => endPoker()}>Abstimmung beenden</button>
              </div>
            )}
      </div>
      </div>
      <div className="contact_list">
      </div>
      <div className="message">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <input type="submit"/>
        </form>
      </div>
    </div>
  );
};

export default Chat;
