import React, { useState, useEffect } from "react";
import queryString from "query-string";
import { Link } from "react-router-dom";
import './Home.css';
import bootstrap from 'bootstrap'
import { header } from "express/lib/request";
import { useLocation } from "react-router";

const Home = ({location}) => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");

  useEffect(() => {
    // get username and roomname from url
    const { name } = queryString.parse(location.search);
    setName(name);
    console.log(name);
    
  }, [location.search]);

  return (
    <div className="wrapper fadeIn Down">
      <Link to="/dashboard">
        <button style={{width:"50px"}}>
          <img src={process.env.PUBLIC_URL + "/icon-dashboard.svg"} alt="Home" title="Dashboard"/>
        </button>
      </Link>
      <div className="form-login">
        <h1>Welcome to Hi5</h1>
      <div className="row">
        <div className="col border-right border-dark">
          <form>
          <div>
            <input
              value={name}
              id="name"
              className="form-control mt-3"
              placeholder="Name"
              type="text"
              onChange={(event) => setName(event.target.value)}
              required
            />
            <div className="invalid-feedback">Bitte Nutzernamen eingeben!</div>
          </div>
          <div>
            <input
              className="form-control mt-3"
              placeholder="Room"
              type="text"
              onChange={(event) => setRoom(event.target.value)}

            />
          </div>
          <Link
            onClick={(e) => (!name || !room ? e.preventDefault() : null)}
            to={`/chat?name=${name}&room=${room}`}
          >
            <button className="btn btn-primary mt-3" type="submit">Sign In</button>
          </Link>
          </form>
      </div>
      <div className="col">
        <Link
            onClick={(e) => (!name ? e.preventDefault() : null)}
            to={`/chat?name=${name}&room=room-${Math.floor(Math.random()* (999999999 - 100000000) + 100000000)}`}
        >
          <button className="btn btn-dark mt-3">Create Room</button>
        </Link>
      </div>
     </div>
    </div>
    </div>
    
  );
};

export default Home;
