import React, { useState } from "react";
import { Link } from "react-router-dom";
import './Home.css';


const Invite = () => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");

  var get_room = window.location.href.split("/").slice(-1);

  return (
    <div className="wrapper fadeIn Down">
      <h1>invite</h1>
      <div className="form-login">
        <h1>Welcome to Hi5</h1>
        <div className="row">
          <div className="col">
            <form>
              <div>
                <input
                  id="name"
                  className="form-control mt-3"
                  placeholder="Name"
                  type="text"
                  onChange={(event) => {setName(event.target.value); setRoom(get_room)}}
                  required
                />
                <div className="invalid-feedback">Bitte Nutzernamen eingeben!</div>
              </div>
              <Link
                onClick={(e) => (!name || !room ? e.preventDefault() : null)}
                to={`/chat?name=${name}&room=${room}`}
              >
                <button className="btn btn-primary mt-3" type="submit">Sign In</button>
              </Link>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};
  
export default Invite;
