import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import './Chat.css'
let socket;

const Chat = ({ location }) => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [pokerState, setPokerState] = useState(false);
  const [pokerMaster, setPokerMaster] = useState(false);
  const [voted, setVote] = useState(false);

  const ENDPOINT = "http://localhost:5000";

  useEffect(() => {
    const { name, room } = queryString.parse(location.search);
    socket = io(ENDPOINT);
    setRoom(room);
    setName(name);

    socket.emit("join", { name, room }, (error) => {
      if (error) {
        alert(error);
      }
    });
  }, [location.search]);


  useEffect(() => {
    socket.on("message", (message) => {
      setMessages((messages) => [...messages, message]);
    });
    // socket.on("roomData", ({ users }) => {
    //   console.log(users);
    //   setUsers(users);
    // });
  }, []);

  useEffect(() => {
    socket.on("planningPoker", (message) => {
      setPokerState(true);

      console.log("State of PlanningPoker: " + pokerState);
    }
    );

    socket.on("pokerMaster", () => {
      setPokerMaster(true);
    });

    socket.on("endPoker", () => {
      setPokerState(false);
      setVote(false);
    });

  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message) {
      socket.emit("sendMessage", { message });
      setMessage("");
    } 
    else {
      console.log("info")
      alert("empty input");
    }
  }; 

  function printPokerValue (wert) {
    //e.preventDefault();
    console.log("funktion ausgef체hrt");
    //setMessage("Gew채hlter Wert: " + value);
    socket.emit("SetPokerValue", 100);
    setVote(true);
  }

  function endPoker() {
    setPokerMaster(false);
    socket.emit("endPoker");
  }

  return (
    <div className="container">
      <div className="personal_info">
        <h1>Hi5 {pokerState}</h1>
      </div>
      <div className="contact_info">
        <h1>{name}</h1> 
      </div>
      <div className="chat">
      {messages.map((val, i) => {
        return (
          <div key={i} className="chatMessage">
            <b>{val.user}</b>
            <br />
            {val.text}
          </div>
        );
      })}
      <div align="center">
        {pokerState && !voted && (
          
          <div>
            Bitte geben Sie Ihre Wertung ab:
            <div className="pokerLabel"> 
              <button onClick={() => printPokerValue(0)}> 0 </button>
              <button onClick={() => printPokerValue(0.5)}> {"171"} </button>
              <button onClick={() => printPokerValue(1)}> 1 </button>
              <button onClick={() => printPokerValue(2)}> 2 </button>
              <button onClick={() => printPokerValue(3)}> 3 </button>
              <button onClick={() => printPokerValue(5)}> 5 </button>
              <button onClick={() => printPokerValue(8)}> 8 </button>
              <button onClick={() => printPokerValue(10)}> 10 </button>
              <button onClick={() => printPokerValue(20)}> 20 </button>
              <button onClick={() => printPokerValue(40)}> 40 </button>
              <button onClick={() => printPokerValue(100)}>100</button>
            </div> 
            
          </div> 
        )}
        {pokerMaster && pokerState &&(
              <div>
                <button onClick={() => endPoker()}>Beenden</button>
              </div>
            )}
      </div>
      </div>
      <div className="contact_list">
      </div>
      <div className="message">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <input type="submit"/>
        </form>
      </div>
    </div>
  );
};

export default Chat;
