import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import './Chat.css'
import ScrollableFeed from 'react-scrollable-feed';

let socket;
var master = false;

const Chat = ({ location }) => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [pokerState, setPokerState] = useState(false);
  //const [pokerMaster, setPokerMaster] = useState(false);
  const [voted, setVote] = useState(false);

  const ENDPOINT = "http://localhost:5000";

  useEffect(() => {
    const { name, room } = queryString.parse(location.search);
    socket = io(ENDPOINT);
    setRoom(room);
    setName(name);

    socket.emit("join", { name, room }, (error) => {
      if (error) {
        alert(error);
      }
    });
  }, [location.search]);

  useEffect(() => {
    socket.on("message", (message) => {
      setMessages((messages) => [...messages, message]);
    });
    
    // socket.on("roomData", ({ users }) => {
    //   console.log(users);
    //   setUsers(users);
    // });
  }, []); 

  useEffect(() => {
    socket.on("planningPoker", () => {
      setPokerState(true);
      console.log("State of PlanningPoker: " + pokerState);
    });
  }, []);

  /*useEffect(() => {
    socket.on("pokerMaster", () => {
      setPokerState([true, true]);
    });
  });*/

  useEffect(() => {
    socket.on("endPoker", () => {
      setPokerState(false);
      setVote(false);
    });
  }); 

  const handleSubmit = (e) => { 
    e.preventDefault();
    if (message) {
      if(message[0] == '#') {
        console.log("# erkannt Client");
        if(!pokerState)
          master = true;
      }
      console.log(master);
      socket.emit("sendMessage", { message });
      setMessage("");
    } else {
      console.log("empty chat input");
    }
  }; 

  function printPokerValue(wert) {
    //e.preventDefault();
    console.log("funktion ausgef체hrt");
    //setMessage("Gew채hlter Wert: " + value);
    socket.emit("setPokerValue", wert);
    setVote(true);
  }

  function endPoker() {  
    master = false;
    setPokerState(false);    
    socket.emit("endPoker");
  }
  
  return (
    <div className="container">
      <div className="personal_info row">
        eins
       
      </div>

      <div className="row">
        <div className="chat">

        </div>

      </div>
    </div>
  )
};

export default Chat;
