import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import ReactDOM from 'react-dom';
import './Chat.css'
let socket;

const Chat = ({ location }) => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [pokerState, setPokerState] = useState(false);

  const ENDPOINT = "http://localhost:5000";

  useEffect(() => {
    const { name, room } = queryString.parse(location.search);
    socket = io(ENDPOINT);
    setRoom(room);
    setName(name);

    socket.emit("join", { name, room }, (error) => {
      if (error) {
        alert(error);
      }
    });
  }, [location.search]);
  useEffect(() => {
    socket.on("message", (message) => {
      setMessages((messages) => [...messages, message]);
    });
    // socket.on("roomData", ({ users }) => {
    //   console.log(users);
    //   setUsers(users);
    // });
  }, []);

  useEffect(() => {
    socket.on("planningPoker", (message) => {
      setPokerState(true);

      console.log("State of PlanningPoker: " + pokerState);
    });
    // socket.on("roomData", ({ users }) => {
    //   console.log(users);
    //   setUsers(users);
    // });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message) {
      socket.emit("sendMessage", { message });
      setMessage("");
    } 
    else {
      console.log("info")
      alert("empty input");
    }
  }; 

  printPokerValue = (e) => {
    e.preventDefault();
    console.log("funktion ausgeführt");
    setMessage("Gewählter Wert: " + e.target.value);
  }

  return (
    <div className="container">
      <div className="personal_info">
        <h1>Hi5 {pokerState}</h1>
      </div>
      <div className="contact_info">
        <h1>{name}</h1> 
      </div>
      <div className="chat">
      {messages.map((val, i) => {
        return (
          <div key={i} className="chatMessage">
            <b>{val.user}</b>
            <br />
            {val.text}
          </div>
        );
      })}
      <div>
        {pokerState && (
          <div>
            <button> 0 </button>
            <button> 1 </button>
            <button> 5 </button>
            <button> 10 </button>
            <button> 20 </button>
            <button> 40 </button>
            <button value="100" id="Poker100" onClick={this.printPokerValue}>100</button>
            <button onClick={() => setPokerState(false)}>TestButton</button>
          </div>
        )}
      </div>
      </div>
      <div className="contact_list">
      </div>
      <div className="message">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <input type="submit"/>
        </form>
      </div>
    </div>
  );
};

export default Chat;
