import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import ScrollableFeed from 'react-scrollable-feed';
import queryString from "query-string";
import io from "socket.io-client";
import './Chat.css'
import { ENDPOINT, URI } from '../config-react.js'
import bootstrap from 'bootstrap';
import {CopyToClipboard} from 'react-copy-to-clipboard';

let socket;

const Chat = ({ location }) => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [pokerState, setPokerState] = useState(false);
  const [is_master, setIsMaster] = useState(false);
  const [has_voted, setHasVoted] = useState(false);

  useEffect(() => {
    // get username and roomname from url
    const { name, room } = queryString.parse(location.search);
    socket = io(ENDPOINT);
    setRoom(room);
    setName(name);

    // join the room 
    socket.emit("join", { name, room }, (error) => {
      if (error) {
        alert(error);
      }
    });
  }, [location.search]);

  useEffect(() => {
    // receive messages from server
    socket.on("message", (message) => {
      setMessages((messages) => [...messages, message]);
    });

    // receive "start planning poker" from server
    socket.on("start_poker", () => {
      setPokerState(true);
    });

    // receive "end planning poker" from server 
    socket.on("end_poker", () => {
      setPokerState(false);
      setHasVoted(false);
    });
  }, []);

  // send message to server
  function send_message(e) { 
    e.preventDefault();
    if (message) {
      if(message.startsWith("#")) {
        setIsMaster(true);
      }
      socket.emit("sendMessage", { message });
      setMessage("");
    }
  }; 

  // send poker value to server
  function send_poker_value(value) {
    socket.emit("set_poker_value", value);
    setHasVoted(true);
  }

  // end planning poker
  function end_poker() {
    setIsMaster(false);
    setPokerState(false);    
    socket.emit("end_poker");
  }

  // crc32 hashing algorithm (minified version)
  function crc32(r) {
    for(var a,o=[],c=0;c<256;c++){a=c;for(var f=0;f<8;f++) a=1&a?3988292384^a>>>1:a>>>1;o[c]=a;}
    for(var n=-1,t=0;t<r.length;t++) n=n>>>8^o[255&(n^r.charCodeAt(t))];
    return(-1^n)>>>0;
  }

  return (
   <div className="fullscreen">

    <nav className="navbar navbar-expand-lg navbar-hi5 navbar-light">
              <a className="navbar-brand display-3" href="/">Hi5</a>
              <div align="center">
                TEst
              </div>
                <ul className="navbar-nav ml-auto mr-4">
                <li className="nav-item active" align="center">
                      Test
                    </li>
                  <div className="row">
                    <li className="nav-item active">
                      <a className="nav-link h3" href="/dashboard"><img style={{width:"40px"}} src={process.env.PUBLIC_URL + "/icon-dashboard.svg"} alt="Home" title="Dashboard"/><span class="sr-only">(current)</span></a>
                    </li>
                    <li className="nav-item active pl-4">
                      <a className="nav-link h3" href="/welcome">
                        <img className="img-fluid" style={{width:"40px"}} src={process.env.PUBLIC_URL + "/icon-info.svg"} alt="Welcome" title="Welcome"/>
                        <span className="sr-only">(current)</span>
                      </a>
                    </li>
                  </div>
                  
                </ul>
          </nav>

   </div>
  );
};

export default Chat;
