import React, { useState } from "react";
import { Link } from "react-router-dom";
import './Login.css';
import bootstrap from 'bootstrap'


const Home = () => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");

  return (
    <div className="wrapper fadeIn Down">
      <div className="form-login">
        <h1>Welcome to Hi5</h1>
      <div className="row">
        <div className="col border-right border-dark">
          <form>
          <div>
            <input
              id="name"
              className="form-control mt-3"
              placeholder="Name"
              type="text"
              onChange={(event) => setName(event.target.value)}
              required
            />
            <div className="invalid-feedback">Bitte Nutzernamen eingeben!</div>
          </div>
          <div>
            <input
              className="form-control mt-3"
              placeholder="Room"
              type="text"
              onChange={(event) => setRoom(event.target.value)}

            />
          </div>
          <Link
            onClick={(e) => (!name || !room ? e.preventDefault() : null)}
            to={`/chat?name=${name}&room=${room}`}
          >
            <button className="btn btn-primary mt-3" type="submit">Sign In</button>
          </Link>
          </form>
      </div>
      <div className="col">
        <Link
            onClick={(e) => (!name ? alert("Bitte Namen eingeben") : null)}
            to={`/chat?name=${name}&room=${Math.floor(Math.random()*100000)}`}
          >
          <button className="btn btn-dark mt-3">Create Room</button>
        </Link>
      </div>
     </div>
    </div>
    </div>
    
  );
};

export default Home;
