import React, { useState } from "react";
import { Link } from "react-router-dom";
import './Home.css';

const Home = () => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");


  (function () {
    'use strict';
    window.addEventListener('load', function () {
      // Fetch all the forms we want to apply custom Bootstrap validation styles to
      var forms = document.getElementsByClassName('needs-validation');
      // Loop over them and prevent submission
      var validation = Array.prototype.filter.call(forms, function (form) {
        form.addEventListener('submit', function (event) {
          if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
          }
          form.classList.add('was-validated');
        }, false);
      });
    }, false);
  })();
  
  return (
    <div className="standart">
    <nav className="navbar navbar-expand-lg navbar-hi5 navbar-light">
          <a className="navbar-brand display-3" href="/">Hi5</a>
            <ul className="navbar-nav ml-auto mr-4">
              <div className="row">
                <li className="nav-item active">
                  <a class="nav-link h3" href="/dashboard"><img style={{width:"40px"}} src={process.env.PUBLIC_URL + "/icon-dashboard.svg"} alt="Home" title="Dashboard"/><span class="sr-only">(current)</span></a>
                </li>
                <li className="nav-item active pl-4">
                  <a className="nav-link h3" href="/welcome">
                    <img style={{width:"40px"}} src={process.env.PUBLIC_URL + "/icon-info.svg"} alt="Welcome" title="Welcome"/>
                    <span class="sr-only">(current)</span>
                  </a>
                </li>
              </div>
              
            </ul>
      </nav>
      <div className="wrapper fadeIn Down">
        
        <div className="form-login">
          <div className="display-3">Welcome to Hi5</div>
          <div className="row">
            <div className="col border-right border-dark">
              <form className="needs-validation" novalidate>
                <div>
                  <input
                    id="name"
                    className="form-control mt-3"
                    placeholder="Name"
                    type="text"
                    onChange={(event) => setName(event.target.value)}
                    required
                  />
                  <div className="invalid-feedback">Bitte Nutzernamen eingeben!</div>
                </div>
                <div>
                  <input
                    className="form-control mt-3"
                    placeholder="Room"
                    type="text"
                    onChange={(event) => setRoom(event.target.value)}
                    required
                  />
                  <div className="invalid-feedback">Bitte Raumnummer eingeben!</div>
                </div>
                <Link
                  onClick={(e) => (!name || !room ? e.preventDefault() : null)}
                  to={`/chat?name=${name}&room=${room}`}
                >
                  <button className="btn btn-primary mt-3" type="submit">Sign In</button>
                </Link>
              </form>
          </div>
          <div className="col">
            <Link
                onClick={(e) => (!name ? e.preventDefault() : null)}
                to={`/chat?name=${name}&room=room-${Math.floor(Math.random()* (999999999 - 100000000) + 100000000)}`}
            >
              <button className="btn btn-dark mt-3 ml-1">Create Room</button>
            </Link>
          </div>
        </div>
      </div>
      </div>
      <div className="row mt-3">
        <div className="col-12">
          <Link to="/welcome">
              <button style={{width:"100px"}} className="button-back btn btn-primary-outline">
                <img src={process.env.PUBLIC_URL + "/icon-info.svg"} alt="Welcome" title="Welcome"/>
              </button>
            </Link>
            <Link to="/dashboard">
              <button style={{width:"70px"}} className="button-welcome btn btn-light mb-3">
                <img src={process.env.PUBLIC_URL + "/icon-dashboard.svg"} alt="Home" title="Dashboard"/>
              </button>
            </Link>
        </div>
        
      </div>
      <script src="https://code.jquery.com/jquery-3.3.1.slim.min.js" integrity="sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo" crossorigin="anonymous"></script>
    </div>
    
  );
};

export default Home;
