import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import io from "socket.io-client";
import './Dashboard.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

let socket;

const Dashboard = () => {
  const [statistics, setStatistics] = useState({total_users: 0, total_rooms: 0, total_messages: 0});

  const ENDPOINT = "http://localhost:5000";

  var data = {
    datasets: [
      {
        label: 'Daily Messages',
        backgroundColor: '#00a884',
        borderColor: '#00a884',
        data: statistics.daily_messages
      }
    ]
  };

  useEffect(() => {
    socket = io(ENDPOINT);

    // save statistics to state machine
    socket.on("statistics", ({statistics}) => {
      setStatistics(statistics);
    });

    // fetch statistics at startup
    socket.emit("statistics");

    // fetch statistics once per minute (debug: once per second)
    const interval = setInterval(() => {
      socket.emit("statistics");
    }, 1*1000);
    return () => clearInterval(interval);

  }, []);
  
  return (
      <div className="wrapper-container">
        <h1 className="dashboard-heading">Dashboard</h1>
        <div className="back-button">
          <Link to="/">
            <button style={{width:"50px"}}>
              <img src={process.env.PUBLIC_URL + "/icon-back-2.svg"} alt="Home" />
            </button>
          </Link>
        </div>
        <div className="statistics-container">
          <div className="small-container total-users-container">
            <img className="user-icon" src={process.env.PUBLIC_URL + "/icon-user.svg"} alt=" "/> 
            <div className="total-users-text">Total Users</div>
            <div className="total-users-count">{statistics.total_users}</div>
          </div>
          <div className="small-container total-rooms-container">
            <img className="room-icon" src={process.env.PUBLIC_URL + "/icon-room.svg"} alt=" "/>
            <div className="total-rooms-text">Total Rooms</div>
            <div className="total-rooms-count">{statistics.total_rooms}</div>
          </div>
          <div className="small-container total-messages-container">
            <img className="message-icon" src={process.env.PUBLIC_URL + "/icon-message.svg"} alt=" "/>
            <div className="total-messages-text">Total Messages</div>
            <div className="total-messages-count">{statistics.total_messages}</div>
          </div>
          <div className="daily-messages-container">
            <div className="daily-messages-text">Daily Messages</div>
            <div className="chartjs-container">
              <Bar data={data} />
            </div>
          </div>
        </div>
      </div>
  );
};

export default Dashboard;
