import React, { useState } from "react";
import { Link } from "react-router-dom";
import './Home.css';
import { URI } from "../config-react.js";
import bootstrap from "bootstrap";
const Home = () => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [validated, setValidated] = useState(false);

  var get_name = window.location.href.match(/([^\/]*)\/*$/)[1];
  if(get_name === URI) { // TODO: RegEx und diese Zeile evtl nochmal Ã¼berarbeiten
    get_name = "";
  }

  function setStates(p_room) {
    setRoom(p_room);

    if(name === "") {
      setName(get_name);
    }
  }

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }
  
    setValidated(true);
  };

  return (
    <div className="standart">

     <nav className="navbar navbar-expand-lg navbar-hi5 navbar-light">
          <a className="navbar-brand display-3" href="/">Hi5</a>
            <ul className="navbar-nav ml-auto mr-4">
              <div className="row">
                <li className="nav-item active">
                  <a className="nav-link h3" href="/dashboard"><img style={{width:"40px"}} src={process.env.PUBLIC_URL + "/icon-dashboard.svg"} alt="Home" title="Dashboard"/><span class="sr-only">(current)</span></a>
                </li>
                <li className="nav-item active pl-4">
                  <a className="nav-link h3" href="/welcome">
                    <img className="img-fluid" style={{width:"40px"}} src={process.env.PUBLIC_URL + "/icon-info.svg"} alt="Welcome" title="Welcome"/>
                    <span className="sr-only">(current)</span>
                  </a>
                </li>
              </div>
              
            </ul>
      </nav>
      <div className="wrapper fadeIn Down">   
      <div className="form-login">
        <div className="display-3 mb-2">Welcome to Hi5</div>
        <form className="was-validated">

        <div className="row" align="center">
          <div className="col pb-3">
            <input
              defaultValue={get_name}
              id="name"
              className="form-control mt-3"
              placeholder="Name"
              maxlength="20"
              onChange={(event) => setName(event.target.value)}
              pattern="[A-Za-z0-9][A-Za-z0-9\s]*"
              required
            />
            <div className="invalid-feedback pl-1">Username is invalid!</div>
          </div>
        </div>
        <div className="row">
          <div className="col-12 font-weight-bold" align="center">
            or
          </div>
        </div>
        <div className="row">
          <div className="col border-right border-dark">
              <div>
                <input
                  className="form-control mt-3"
                  pattern="[A-Za-z0-9]*"
                  placeholder="Room"
                  maxlength="100"
                  type="text"
                  onChange={(event) => setStates(event.target.value)}
                />
              </div>
              
          
          </div>
          <div className="col mb-0">
            
            
          </div>
        </div>
        <div className="row">
          <div className="col border-right border-dark">
            <Link
                  onClick={(e) => (!name || !room ? e.preventDefault() : null)}
                  to={`/chat?name=${name}&room=${room}`}
                >
                  <button className="btn btn-dark mt-3" type="submit">Sign In</button>
            </Link>
          </div>
          <div className="col">
          <Link
              onClick={(e) => (!name ? e.preventDefault() : null)}
              to={`/chat?name=${name}&room=room-${Math.floor(Math.random()* (999999999 - 100000000) + 100000000)}`}
            >
              <button className="btn btn-dark mt-3 align-bottom" onMouseEnter={() => setStates(room)}>Create Room</button>
            </Link>
          </div>
        </div>
        </form>
      </div>
      </div>
      
      

      <script src="https://code.jquery.com/jquery-3.3.1.slim.min.js" integrity="sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo" crossorigin="anonymous"></script>
    </div>
    
  );
};

export default Home;
